ClientEvents.ClientValidation:
        
        function(s,e)
        {
            if(s.getValue()=="")
            {
                e.returnValue=false;
                e.message="必填字段不能为空XML";
                return;
            }
            
            if (s.length >5)
            {
                e.returnValue=false;
                e.message = "录入的字符超过最大数量：10XML";
                return;
            }                    
            e.returnValue=true;
        }

        
        function(s,e)
        {
            if(s.getValue()=="")
